// 获取AI Agent的输出文本
const fullText = $('set_cover_letter').first().json.output;

// 按自然段落分割文本
const paragraphs = fullText.split("\n\n");

// 提取第一段作为标题（可选）
const title = paragraphs[0].trim();

// 创建块内容
const blocks = [];

paragraphs.forEach((paragraph, index) => {
  if (paragraph.trim().length > 0) {
    // 确保段落不超过2000字符
    if (paragraph.length > 2000) {
      // 如果超过，进一步分割
      let startIdx = 0;
      while (startIdx < paragraph.length) {
        const chunk = paragraph.substring(startIdx, Math.min(startIdx + 1900, paragraph.length));
        blocks.push({
          "type": "paragraph",
          "paragraph": {
            "rich_text": [{
              "type": "text",
              "text": {
                "content": chunk
              }
            }]
          }
        });
        startIdx += 1900;
      }
    } else {
      // 如果段落长度合适，直接添加
      blocks.push({
        "type": "paragraph",
        "paragraph": {
          "rich_text": [{
            "type": "text",
            "text": {
              "content": paragraph
            }
          }]
        }
      });
    }
  }
});

// 返回结果，包含标题和块
return {
  json: {
    title: title,
    blocks: blocks
  }
};

