// 标准化职位数据的函数节点
function normalizeJobData(inputData) {
  // 创建含默认值的标准职位对象
  const standardJob = {
    id:'',
    company_name: '',
    job_title: '',
    application_url: '',
    location: '',
    description: '',
    industry: 'Not specified',
    flexibility: 'Not specified',
    salary_range: 'Not specified',
    status: 'new',
    source: 'external',
    created_at: new Date().toISOString()
  };

  // 尝试映射输入字段 - 使用多种可能的命名
  // 公司名称
  standardJob.id = inputData.id || inputData.ID || inputData.job_id
  // 确保ID始终是字符串
  if (inputData.id !== undefined && inputData.id !== null) {
    standardJob.id = String(inputData.id); // 强制转换为字符串
  } else if (inputData.database_id !== undefined && inputData.database_id !== null) {
    standardJob.id = String(inputData.database_id);
  }

  standardJob.company_name = inputData.company_name || inputData.company || 
                            inputData.companyName || inputData.employer || 'Unknown Company';

  // 职位名称
  standardJob.job_title = inputData.job_title || inputData.title || 
                         inputData.position || inputData.role || 'Unknown Position';

  // 应用链接
  standardJob.application_url = inputData.application_url || inputData.url || 
                               inputData.link || inputData.apply_url || inputData.applyUrl || '';

  // 地点
  standardJob.location = inputData.location || inputData.place || 
                        inputData.job_location || inputData.city || '';

  // 描述
  standardJob.description = inputData.description || inputData.job_description || 
                           inputData.desc || inputData.details || inputData.desription || inputData.descriptionText || '';

  // 行业
  standardJob.industry = inputData.industry || inputData.sector || 
                        inputData.field || standardJob.industry;

  // 灵活性/工作安排
  standardJob.flexibility = inputData.flexibility || inputData.work_type || 
                           inputData.arrangement || standardJob.flexibility;

  // 薪资范围
  standardJob.salary_range = inputData.salary_range || inputData.salary || 
                            inputData.compensation || standardJob.salary_range;

  // 来源可以被覆盖
  if (inputData.source) {
    standardJob.source = inputData.source;
  }

  // 日志记录，帮助调试
  console.log('原始数据:', JSON.stringify(inputData));
  console.log('标准化后:', JSON.stringify(standardJob));

  return standardJob;
}

